Content.Code:
double ch, sh;

t = 0.0;					// Time

state[0] = Math.toRadians(phiDegrees);	// Eulerian angles
state[1] = Math.toRadians(thetaDegrees);
state[2] = Math.toRadians(psiDegrees);
state[3] = phid0;				// Generalized velocities
state[4] = thetad0;
state[5] = psid0;

scaleW = 0.0;				// reset scale

canContinue = false;

_view.resetTraces();

if      (Ix < tol) Ix = tol;		// Range
else if (Ix > 2.0) Ix = 2.0;
if      (Iy < tol) Iy = tol;
else if (Iy > 2.0) Iy = 2.0;
if      (Iz < tol) Iz = tol;
else if (Iz > 2.0) Iz = 2.0;
if (Ix >= Iy+Iz) Ix = Iy+Iz;
if (Iy >= Ix+Iz) Iy = Ix+Iz;
if (Iz >= Ix+Iy) Iz = Ix+Iy;

dx = Math.sqrt(Iy+Iz-Ix);		// Dimensions
dy = Math.sqrt(Ix+Iz-Iy);
dz = Math.sqrt(Ix+Iy-Iz);
dd = dx;
if (dy > dd) dd = dy;
if (dz > dd) dd = dz;
dd *= 0.7;

double cf = Math.cos(state[0]), sf = Math.sin(state[0]),
       ch = Math.cos(state[1]), sh = Math.sin(state[1]),
       cs = Math.cos(state[2]), ss = Math.sin(state[2]),
       phid   = state[3],
       thetad = state[4],
       psid   = state[5];

axes[0] = cf*cs - ch*sf*ss;		// Principal axes
axes[1] = cs*sf + cf*ch*ss;
axes[2] = sh*ss;
axes[3] = -(ch*cs*sf) - cf*ss;
axes[4] = cf*ch*cs - sf*ss;
axes[5] = cs*sh;
axes[6] = sf*sh;
axes[7] = -(cf*sh);
axes[8] = ch;

wx  = phid*sh*ss+thetad*cs;		// Body frame
wy  = phid*sh*cs-thetad*ss;		
wz  = phid*ch+psid;
Lx  = Ix*wx;
Ly  = Iy*wy;
Lz  = Iz*wz;
wsx = thetad*cf+psid*sf*sh;		// Space frame
wsy = thetad*sf-psid*cf*sh;
wsz = phid+psid*ch;
Lsx = (cf*cs-sf*ch*ss)*Lx+(-cf*ss-sf*ch*cs)*Ly+(sf*sh) *Lz;
Lsy = (sf*cs+cf*ch*ss)*Lx+(-sf*ss+cf*ch*cs)*Ly+(-cf*sh)*Lz;
Lsz = (sh*ss)         *Lx+(sh*cs)          *Ly+(ch)    *Lz;

if (scaleW == 0.0) {			// Scale at initial moment
  scaleW = 1.1*dd/Math.sqrt(wx*wx+wy*wy+wz*wz);
  scaleL = 1.2*dd/Math.sqrt(Lx*Lx+Ly*Ly+Lz*Lz);
}

public double[] eqs(double t, double[] state) {
  double phi    = state[0], 
         theta  = state[1], 
         psi    = state[2],
         phid   = state[3], 
         thetad = state[4],
         psid   = state[5];
  double ch = Math.cos(theta), sh = Math.sin(theta),
         cf = Math.cos(phi),   sf = Math.sin(phi),
         cs = Math.cos(psi),   ss = Math.sin(psi);
  double [] ders = new double[6];

  ders[0] = phid;
  ders[1] = thetad;
  ders[2] = psid;
  ders[3] = -((cs*(Ix-Iy)*(Ix+Iy-Iz)*phid*(ch*phid+psid)*sh*ss+
             (ch*(cs*cs*(Ix-Iy)+Iy)*(Ix+Iy-Iz)*phid+
             (Iy*(-Ix+Iy-Iz)+cs*cs*(Ix-Iy)*(Ix+Iy-Iz))*psid)*thetad)/(Ix*Iy*sh));
  ders[4] = -((-1+2*cs*cs)*(Ix-Iy)*(Ix+Iy-Iz)*phid*(ch*phid+psid)*sh+
             phid*(-(ch*(Ix*Ix+Iy*Iy-(Ix+Iy)*Iz)*phid)+
             (-(Ix-Iy)*(Ix-Iy)+(Ix+Iy)*Iz)*psid)*sh-
             2*cs*(Ix-Iy)*(Ix+Iy-Iz)*(ch*phid+psid)*ss*thetad)/(2*Ix*Iy);
  ders[5] = (cs*cs*(Ix-Iy)*(ch*(Ix+Iy-Iz)*Iz*(ch*phid+psid)+
             2*Ix*Iy*phid*sh*sh)*thetad+Iy*(ch*ch*(Ix+Iy-Iz)*Iz*phid-
             ch*Iz*(Ix-Iy+Iz)*psid+Ix*(-Ix+Iy+Iz)*phid*sh*sh)*thetad+
             cs*(Ix-Iy)*sh*ss*(ch*(Iy-Iz)*Iz*phid*(ch*phid+psid)+
             Ix*(ch*Iz*phid*(ch*phid+psid)-
             Iy*((-1+ch*ch)*phid*phid+thetad*thetad))))/(Ix*Iy*Iz*sh);
  return ders;
}

