double ch, sh;

t = 0.0;					// Time

state[0] = 0.0;				// Eulerian angles
state[1] = Math.toRadians(thetaDegrees);
state[2] = 0.0;
state[3] = phid0;				// Generalized velocities
state[4] = thetad0;
state[5] = psid0;

axesB[1] = 				// Body cone
axesB[2] =
axesB[3] =
axesB[5] =
axesB[6] =
axesB[7] = 0.0;
axesB[0] =
axesB[4] =
axesB[8] = 1.0;

if      (Iz < tol) Iz = tol;		// Range
else if (Iz >= 2.0*Ix)  Iz = 2.0*Ix;

dx = 					// Dimensions
dy = Math.sqrt(Iz);
dz = Math.sqrt(2.0*Ix-Iz);
dd = dx;
if (dz > dd) dd = dz;
dd *= 0.7;

double cf = Math.cos(state[0]), sf = Math.sin(state[0]),
       ch = Math.cos(state[1]), sh = Math.sin(state[1]),
       cs = Math.cos(state[2]), ss = Math.sin(state[2]),
       phid   = state[3],
       thetad = state[4],
       psid   = state[5];

axes[0] = cf*cs - ch*sf*ss;		// Principal axes
axes[1] = cs*sf + cf*ch*ss;
axes[2] = sh*ss;
axes[3] = -(ch*cs*sf) - cf*ss;
axes[4] = cf*ch*cs - sf*ss;
axes[5] = cs*sh;
axes[6] = sf*sh;
axes[7] = -(cf*sh);
axes[8] = ch;

wx  = phid*sh*ss+thetad*cs;		// Body frame
wy  = phid*sh*cs-thetad*ss;		
wz  = phid*ch+psid;
scaleW = 1.1*dd/Math.sqrt(wx*wx+wy*wy+wz*wz);
Lx  = Ix*wx;
Ly  = Ix*wy;
Lz  = Iz*wz;
scaleL = 1.2*dd/Math.sqrt(Lx*Lx+Ly*Ly+Lz*Lz);
wsx = thetad*cf+psid*sf*sh;		// Space frame
wsy = thetad*sf-psid*cf*sh;
wsz = phid+psid*ch;
Lsx = (cf*cs-sf*ch*ss)*Lx+(-cf*ss-sf*ch*cs)*Ly+(sf*sh) *Lz;
Lsy = (sf*cs+cf*ch*ss)*Lx+(-sf*ss+cf*ch*cs)*Ly+(-cf*sh)*Lz;
Lsz = (sh*ss)         *Lx+(sh*cs)          *Ly+(ch)    *Lz;

double wr;

scaleC = .75*scaleW;		// Smaller that angular velocity

wr = Math.sqrt(wx*wx+wy*wy);	// Radius

bd = 2.0*wr*scaleC;		// Diameter

bx =				// Center
by = 0.0;
bz = wz*scaleC;

bh = -bz;			// Height

/*
axesB[0] = wx/wr;			// Orientation if we want this cone spinning
axesB[1] = wy/wr;
axesB[3] = -axesB[1];
axesB[4] = axesB[0];
*/

double L2, wL, wrx, wry, wrz, whx, why, whz, wr, wh, W, nx, ny, nz, ca, sa;

L2 = Lx*Lx+Ly*Ly+Lz*Lz;		// L.L
wL = (wx*Lx+wy*Ly+wz*Lz)/L2;	// w.L/L2

whx = wL*Lx;			// wr = w.L/L2 L
why = wL*Ly;
whz = wL*Lz;

wrx = wx-whx;			// wh = w - wr
wry = wy-why;
wrz = wz-whz;

wh = Math.sqrt(whx*whx+why*why+whz*whz);	// |wh|   height
wr = Math.sqrt(wrx*wrx+wry*wry+wrz*wrz);     // |wr| radius

scaleS = scaleC;			// Scale

sx = whx*scaleS;			// Center
sy = why*scaleS;
sz = whz*scaleS;

sh = -wh*scaleS;			// Height
sd = 2.0*wr*scaleS;		// Radius

axesS[6] = whx /= wh;		// n3 = wh / |wh|
axesS[7] = why /= wh;
axesS[8] = whz /= wh;

wrx /= wr;			// n1 = wr / |wr|
wry /= wr;
wrz /= wr;

W = (Iz/Ix-1.0)*wz*bd/sd;		// Angular velocity
if (W > 0.0) W = -W;
ca = Math.cos(W*t);		// Rotation about L
sa = Math.sin(W*t);
nx = (ca+whx*whx*(1-ca))     *wrx+(-whz*sa+whx*why*(1-ca))*wry+(why*sa+whx*whz*(1-ca)) *wrz;
ny = (whz*sa+whx*why*(1-ca)) *wrx+(ca+why*why*(1-ca))     *wry+(-whx*sa+why*whz*(1-ca))*wrz;
nz = (-why*sa+whx*whz*(1-ca))*wrx+(whx*sa+why*whz*(1-ca)) *wry+(ca+whz*whz*(1-ca))     *wrz;

axesS[0] = nx;			// n1 after rotation
axesS[1] = ny;
axesS[2] = nz;

axesS[3] = (why*nz-whz*ny);	// n2 = n3 x n1 
axesS[4] = (whz*nx-whx*nz);
axesS[5] = (whx*ny-why*nx);

public double[] eqs(double t, double[] state) {
  double phi    = state[0], 
         theta  = state[1], 
         psi    = state[2],
         phid   = state[3], 
         thetad = state[4],
         psid   = state[5];
  double ch = Math.cos(theta), sh = Math.sin(theta);
  double [] ders = new double[6];

  ders[0] = phid;
  ders[1] = thetad;
  ders[2] = psid;
  ders[3] = ((ch*(-2*Ix + Iz)*phid + Iz*psid)*thetad)/(Ix*sh);
  ders[4] = (phid*(ch*(Ix - Iz)*phid - Iz*psid)*sh)/Ix;
  ders[5] = (((1 + ch*ch)*Ix*phid - ch*Iz*(ch*phid + psid))*thetad)/(Ix*sh);
  return ders;
}

