for (int i=0; i<26; i++) line_polY[i]    = 98-line_polY[i];

for (int i=0; i<GRID_LEVELS; i++) {
  gridLevels[i] = (-YMAX + (2*i*YMAX)/(GRID_LEVELS-1))/INIT_SCALE/1.3;
  for (int j=0; j<nMarkers; j++) {
    gridPoints[i*nMarkers+j][0] = lineXmin + (j*lineLength)/(nMarkers-1);
    gridPoints[i*nMarkers+j][1] = gridLevels[i];
  }
}

for (int i=0; i<nMarkers; i++) markersX[i] = xCoord[i*5];

double ssourceC = sourceC*1.0e-12;
double lloadC = loadC*1.0e-12;
time += 1;
realTime +=deltaTime;

// Compute the source
double E_t = 0.0;
if (sourceOn) {
  if (sourceF>0.0) {
    double norm_angle = ((realTime*sourceF*1.0e6) % 1) * TWO_PI;
    if (sourceACTypeSine) E_t = E_t + Math.sin(norm_angle);
    if (sourceACTypeTriangle) {
      if (norm_angle<=HALF_PI)        E_t = E_t + norm_angle/HALF_PI;
      else if (norm_angle<=T_HALF_PI) E_t = E_t + 2.0 - norm_angle/HALF_PI;
      else                            E_t = E_t + norm_angle/HALF_PI - 4.0;
    }
    if (sourceACTypeSquare) {
      if (norm_angle<=Math.PI) E_t = E_t + 1.0;
      else                     E_t = E_t - 1.0;
    }
    if (sourceACTypeCustom) {
    }
  }
  E_t = E_t + sourceV;
}

// Store previous values
int i;
int nMax = nPoints-1;
for (i = 1; i<nMax; i++) {
  sumV[i] = voltage[i+1]   + voltage[i-1];
  difV[i] = voltage[i+1]   - voltage[i-1];
  sumI[i] = intensity[i+1] + intensity[i-1];
  difI[i] = intensity[i+1] - intensity[i-1];
}

// ---- first point algorithm
i = 0;
voltage[i] =  ( E_t*(1.0/sourceR + ssourceC/deltaTime) -
		ssourceC*(E_t_ant-voltage[i])/deltaTime +
                (voltage[i+1]-Z[i]*intensity[i+1])/(A[i]*Z[i]) ) /
				   (1.0/sourceR + ssourceC/deltaTime + B[i]/(A[i]*Z[i]));
intensity[i] = ( (voltage[i]*B[i]) - (voltage[i+1]-Z[i]*intensity[i+1]) ) / (A[i]*Z[i]);

// ---- last point algorithm
i = nPoints-1;
if (loadTypeNone) { // loadR = infinity
  voltage[i]   = (intensity[i-1]*Z[i] + voltage[i-1])/B[i];
  intensity[i] = 0.0;
  }
else if (loadTypeLine) { // loadR = 0;
  voltage[i]   = 0.0;
  intensity[i] = (voltage[i-1]/Z[i] + intensity[i-1])/A[i];
  }
else if (loadTypeR) { /* Order is important here */
  intensity[i] = (intensity[i-1]*Z[i] + voltage[i-1])/ (B[i]*loadR + Z[i]*A[i]);
  voltage[i]   = (-Z[i]*A[i]*intensity[i] + intensity[i-1]*Z[i] +
			    voltage[i-1])/B[i];
  }
else { // R and C
  intensity[i] = ((intensity[i-1]*Z[i]+voltage[i-1])/B[i] - (voltage[i]-intensity[i]*loadR))/
		 (loadR + deltaTime/lloadC + Z[i]*A[i]/B[i]);
  voltage[i]   = (-Z[i]*A[i]*intensity[i] + intensity[i-1]*Z[i] +
			    voltage[i-1])/B[i];
  }

// ---- middle points algorithm
if (lineType==0) { // No discontinuities
  for (i=1; i<nMax; i++) {
    voltage[i]   =  ( sumV[i] - aux1[i]*difI[i]) / (2.0*B[i]);
    intensity[i] =  ( sumI[i] - aux2[i]*difV[i]) / (2.0*A[i]);
  }
}
else {  // There are discontinuities at leftPoint and rightPoint
  // Special code for leftPoint
  i = leftPoint;
  double sum = (voltage[i-1]+Z[i-1]*intensity[i-1])/B[i-1];
  double dif = (voltage[i+1]-Z[i+1]*intensity[i+1])/B[i+1];
  intensity[i] = (sum-dif)/(A[i-1]*Z[i-1]/B[i-1] + A[i+1]*Z[i+1]/B[i+1]);
  voltage[i] = -A[i-1]*Z[i-1]*intensity[i]/B[i-1] + sum;
  // Same code for rightPoint
  i = rightPoint;
  sum = (voltage[i-1]+Z[i-1]*intensity[i-1])/B[i-1];
  dif = (voltage[i+1]-Z[i+1]*intensity[i+1])/B[i+1];
  intensity[i] = (sum-dif)/(A[i-1]*Z[i-1]/B[i-1] + A[i+1]*Z[i+1]/B[i+1]);
  voltage[i] = -A[i-1]*Z[i-1]*intensity[i]/B[i-1] + sum;
  // Rest of the points
  for (i=1; i<nMax; i++) {
    if (i==leftPoint || i==rightPoint) continue; // Already done
    voltage[i]   =  ( sumV[i] - aux1[i]*difI[i]) / (2.0*B[i]);
    intensity[i] =  ( sumI[i] - aux2[i]*difV[i]) / (2.0*A[i]);
  }
}

E_t_ant = E_t;

for (int i=0; i<nPoints; i++) {
  incident[i] = voltage[i]+intensity[i]*DEF_Z;
  reflected[i] = voltage[i]-intensity[i]*DEF_Z;
}
for (int i=0; i<nArrows; i++) {
  int i5 = i*10;
  vArrows[i] = voltage[i5]*ymax/YMAX_SCALED/2;
  i1Arrows[i] =  intensity[i5]*DEF_Z*xmax/XMAX/2;
  i2Arrows[i] = -i1Arrows[i];
  vArrowsY[i] = 56.5-vArrows[i]/2; // 56.5 is the center of the panel
  i1ArrowsX[i] = xArrows[i] - i1Arrows[i]/2;
  i2ArrowsX[i] = xArrows[i] - i2Arrows[i]/2;
}
for (int i=0; i<nMarkers; i++) {
  int i5 = i*5;
  markersVoltage[i] = voltage[i5]*scale;
  markersIntensity[i] = intensity[i5]*DEF_Z*scale;
}

public void switchPanels () {
  int hPanel = _view.center_panel.getHeight();
  int wTotal = _view.frame.getWidth();
  int hExtra = _view.frame.getHeight()-hPanel;
//  int hChild = _view.center_panel.getComponent(0).getHeight();

  int panelsAfter = 0;
  _view.center_panel.removeAll();
  if (showLinePanel) {
    _view.center_panel.add (_view.line_panel);
    panelsAfter++;
  }
  if (showVoltagePanel) {
    _view.center_panel.add (_view.voltage_panel);
    panelsAfter++;
  }
  if (showCurrentPanel) {
    _view.center_panel.add (_view.current_panel);
    panelsAfter++;
  }
  if (showIncidentPanel) {
    _view.center_panel.add (_view.incident_panel);
    panelsAfter++;
  }
  if (showReflectedPanel) {
    _view.center_panel.add (_view.reflected_panel);
    panelsAfter++;
  }
  if (panelsAfter==0) { // Always show at least one panel
    showLinePanel = true;
    _view.center_panel.add (_view.line_panel);
    panelsAfter++;
  }
  _view.center_panel.validate();
  int newH = hExtra + (panelsAfter*hPanel)/panelsShown;
  _view.frame.setSize(wTotal,newH);
  panelsShown = panelsAfter;
}

public void switchSource (boolean on) {
  if (on) sourceLineColor = java.awt.Color.RED;
  else sourceLineColor = java.awt.Color.GREEN;
  sourceLineX = - sourceLineX;
  sourceOn = on;
}

public void resetLine() {
  _pause();
  time = realTime = 0.0;
  E_t_ant = 0.0;
  for (int i=0; i<nPoints; i++) {
    voltage   [i] = 0.0;
    intensity [i] = 0.0;
  }
  // updateAuxiliaryValues(); Not needed???
}

public void setParametersDefaults() {
  scale = 1.0;
  Y_BOTTOM = YMIN_SCALED/1.5 * scale;
  Y_RANGE = (YMAX_SCALED - YMIN_SCALED) /1.5 * scale;
  delay = 10;
  _setDelay((int) delay);
  editMode = false;
  showGrid = false;
}
public void setSourceDefaults() {
  switchSource(false);
  sourceC = 0; sourceR = 50.0; sourceF = 300.0; sourceV = 0.0;
  sourceACTypeSine = true; sourceACTypeTriangle = sourceACTypeSquare = sourceACTypeCustom = false;
  realTime = (oldSourceF*realTime)/sourceF;
  oldSourceF = sourceF;
}

public void setLoadDefaults() {
  loadR = 50.0; loadC = 10.0;
  loadTypeNone = true; loadTypeLine = loadTypeR = loadTypeRC = false;
}

public void setLineDefaults() {
  lineL = 1500.0/9.0; lineC = 600.0/9.0; lineR = 0.0; oneoverlineG=Double.POSITIVE_INFINITY; linel=2.0;
  lineZ = Math.sqrt ((1000.0*lineL)/lineC);
  linev = 1.0e5/Math.sqrt(10.0*lineL*lineC);
  R_at_profile = 200.0;
  Rp_at_profile = 50.0;
  updateAuxiliaryValues();
  setLineType (0);
}

public void setDefaults() {
  time = realTime = 0.0;
  deltaTime = 0.01;
  E_t_ant = 0.0;
  setParametersDefaults();
  setSourceDefaults();
  setLoadDefaults();
  setLineDefaults();
  resetLine();
}

public void updateAuxiliaryValues () {
  double speed = 1.0e11/Math.sqrt(10.0*SPEED_MAXIMUM);
  deltaTime =  1.0*linel /((nPoints-1)*speed);
  leftPoint = rightPoint = -1;
  for (int i=0; i<nPoints; i++) {
    Z[i] = Math.sqrt ((1000.0*lineL)/lineC);
    A[i] = Math.exp ( (1.0e9*lineR*deltaTime)/lineL);
    B[i] = Math.exp(deltaTime/(lineC*oneoverlineG*1.0e-12));
    aux2[i] = ( ( (1.0e9*lineR)/lineL - 1.0e12/(oneoverlineG*lineC) ) /2.0 )*deltaTime;
    aux1[i] = (1.0 - aux2[i]) * Z[i];
    aux2[i] = (1.0 + aux2[i]) / Z[i];
  }
}

public void setLineType (int type) {
  _view.line_uniform.setSelected(false);
  _view.line_Rprofile.setSelected(false);
  _view.line_Gprofile.setSelected(false);
  switch (lineType=type) {
    case 1  : _view.line_Rprofile.setSelected(true); setLineRprofile(); break;
    case 2  : _view.line_Gprofile.setSelected(true); setLineGprofile(); break;
    default : _view.line_uniform.setSelected(true);  updateAuxiliaryValues(); break;
  }
}

private void setLineRprofile () {
  updateAuxiliaryValues();
  double center = lineXmin + lineLength/2;
  double start = center - middleAreaLength/2, end = center + middleAreaLength/2;
  for (int i=0; i<nPoints; i++) {
    if (xCoord[i]<start) { leftPoint = i+1; continue; }
    if (xCoord[i]>end) { rightPoint = i-1; break; }
    A[i] = Math.exp ( (1.0e9*R_at_profile*deltaTime)/lineL);
    aux2[i] = ( ( (1.0e9*R_at_profile)/lineL - 1.0e12/(oneoverlineG*lineC) ) /2.0 )*deltaTime;
    aux1[i] = (1.0 - aux2[i]) * Z[i];
    aux2[i] = (1.0 + aux2[i]) / Z[i];
  }
}

private void setLineGprofile () {
  updateAuxiliaryValues();
  double center = lineXmin + lineLength/2;
  double start = center - middleAreaLength/2, end = center + middleAreaLength/2;
  for (int i=0; i<nPoints; i++) {
    if (xCoord[i]<start) { leftPoint = i+1; continue; }
    if (xCoord[i]>end) { rightPoint = i-1; break; }
    B[i] = Math.exp(deltaTime/(lineC*Rp_at_profile*1.0e-12));
    aux2[i] = ( ( (1.0e9*lineR)/lineL - 1.0e12/(Rp_at_profile*lineC) ) /2.0 )*deltaTime;
    aux1[i] = (1.0 - aux2[i]) * Z[i];
    aux2[i] = (1.0 + aux2[i]) / Z[i];
  }
}

public void checkLoadType() {
  if (! (loadTypeLine || loadTypeR || loadTypeRC) ) loadTypeNone = true;
}

public void computeVoltageFromMarker() {
  int marker = markerMoved;
  markersX[marker] = xCoord[marker*5];
  double valueAtMarker = markersVoltage[marker];
  if (showGrid) {
    int level = -1;
    for (int i=0; i<GRID_LEVELS; i++) {
      if (valueAtMarker<=gridLevels[i]) { level = i; break; }
    }
    if (level<0) valueAtMarker = gridLevels[GRID_LEVELS-1];
    else valueAtMarker = gridLevels[level];
  }
  if (marker>0) {
    double delta = (valueAtMarker - markersVoltage[marker-1])/5.0;
    for (int i=0, point=marker*5; i<5; i++,point--) voltage[point] = (valueAtMarker - i*delta)/scale;
  }
  if (marker<nMarkers-1) {
    double delta = (markersVoltage[marker+1] - valueAtMarker)/5.0;
    for (int i=0, point=marker*5; i<5; i++,point++) voltage[point] = (valueAtMarker + i*delta)/scale;
  }
}

public void computeIntensityFromMarker() {
  int marker = markerMoved;
  markersX[marker] = xCoord[marker*5];
  double valueAtMarker = markersIntensity[marker];
  if (showGrid) {
    int level = -1;
    for (int i=0; i<GRID_LEVELS; i++) {
      if (valueAtMarker<=gridLevels[i]) { level = i; break; }
    }
    if (level<0) valueAtMarker = gridLevels[GRID_LEVELS-1];
    else valueAtMarker = gridLevels[level];
  }
  if (marker>0) {
    double delta = (valueAtMarker - markersIntensity[marker-1])/5.0;
    for (int i=0, point=marker*5; i<5; i++,point--) intensity[point] = (valueAtMarker - i*delta)/scale/DEF_Z;
  }
  if (marker<nMarkers-1) {
    double delta = (markersIntensity[marker+1] - valueAtMarker)/5.0;
    for (int i=0, point=marker*5; i<5; i++,point++) intensity[point] = (valueAtMarker + i*delta)/scale/DEF_Z;
  }
}

public void saveToDisk () {
  javax.swing.JFileChooser fileChooser = org.opensourcephysics.display.OSPRuntime.createChooser("Datafiles",new String[]{"dat","xml"});
  String targetName = org.opensourcephysics.display.OSPRuntime.chooseFilename(fileChooser,_view.top_panel,true);
  if (targetName!=null) {
    if (! (targetName.toLowerCase().endsWith(".dat") || targetName.toLowerCase().endsWith(".xml"))) targetName += ".xml";
    _saveState(targetName);
  }
}

public void readFromDisk () {
  javax.swing.JFileChooser fileChooser = org.opensourcephysics.display.OSPRuntime.createChooser("Data files",new String[]{"dat","xml"});
  String targetName = org.opensourcephysics.display.OSPRuntime.chooseFilename(fileChooser,_view.top_panel,false);
  if (targetName!=null) _readState(targetName);
}

// Overwrites its super
public boolean _readState (String targetName) {
  boolean result = super._readState(targetName);
  if (result) {
    switchPanels();
    setLineType(lineType);
  }
  return result;
}

public void action1() {
  resetLine();
  setLineType(1); // Serial Resistor
  sourceV = 1.0;
  // disable any AC type
  sourceACTypeSine = sourceACTypeTriangle = sourceACTypeSquare = sourceACTypeCustom = false;
  // Sets load line
  loadTypeNone = loadTypeLine = loadTypeRC = false;
  loadTypeR = true;

  switchSource(true); // Turns on the source
  _play(); // Plays the simulation
}

