public double dlinear_T1 (double ldT1, double lVh) {
    return (ldT1 + b11*lVh);  
}
public double daux_T1 (double lT1, double ldT1, double lVh) {
    return (-a21*lT1 - a11*ldT1 + Beta21*lVh);  
}
public double dlinear_T2 (double ldT2, double lVh) {
    return (ldT2 + b12*lVh);  
}
public double daux_T2 (double lT2, double ldT2, double lVh) {
    return (-a22*lT2 - a12*ldT2 + Beta22*lVh);  
}
public double dlinear_T3 (double ldT3, double lVh) {
    return (ldT3 + b13*lVh);  
}
public double daux_T3 (double lT3, double ldT3, double lVh) {
    return (-a23*lT3 - a13*ldT3 + Beta23*lVh);  
}

// Modifying control parameters
private void setControl (int _opt) {
  switch (_opt) {
    case 1 : initControl (pidControl1,linear_T1,Kc1,dt,Ti1,Td1,(Ti1/10),1.0,1.0,vmin1,vmax1); break;
    case 2 : initControl (pidControl2,linear_T2,Kc2,dt,Ti2,Td2,(Ti2/10),1.0,1.0,vmin2,vmax2); break;
    case 3 : initControl (pidControl3,linear_T3,Kc3,dt,Ti3,Td3,(Ti3/10),1.0,1.0,vmin3,vmax3); break;
  }
}

// used when any of the affected control parameters is changed interactively
public void resetControls (int _left) {
  switch (_left) {
      case 1 : setControl (1); K=Kc1;Ti=Ti1;Td=Td1; break;
      case 2 : setControl (2); K=Kc2;Ti=Ti2;Td=Td2; break;
      case 3 : setControl (3); K=Kc3;Ti=Ti3;Td=Td3; break;
  }
}

// Changes the saturation limits for the pid's
public void changeControl (int _opt) {
  switch (_opt) {
    case 1 : 
      changeSaturation (pidControl1, vmin1, vmax1); 
      break;
    case 2 : 
      changeSaturation (pidControl2, vmin2, vmax2); 
      break;
    case 3 : 
      changeSaturation (pidControl3, vmin3, vmax3); 
      break;  
  }
}

// This page implements a generic PID control

/* --------------------- Control parameters ---------------------------------
      0=oldValue, 1=reference, 2=Kp, 3=B, 4=Vmin, 5=Vmax
      ---- private variables ----
      6=Bi, 7=Ar, 8=Ad, 9=Bd, 10=Iaction, 11=Daction
----------------------------------------------------------------------------------------- */

// The pid control also requires the external boolean variable
//  'pidControl' that indicates whether to use or not the integral action

private void initControl (double _control[], 
                                      double _init, double _kp, double _ts, double _ti, double _td, 
                                     double _tt, double _n, double _b, double _min, double _max) {
  // Input parameters
  _control[0] = _init;
  _control[1] = _kp;
  _control[2] = _b;
  _control[3] = _min;
  _control[4] = _max;
  // Computed variables
  _control[5] = _kp * _ts / _ti;                 // Bi = Kp*Ts/Ti;
  _control[6] = _ts / _tt;                          // Ar = Ts/Tt;
  _control[7] = _td / (_td + _n * _ts);      // Ad = Td/(Td+N*Ts);
  _control[8] = _kp * _n * _control[7];    // Bd = Kp*N*Ad;
  _control[9] = Vh;                                // I = 0.0
  _control[10] = 0.0;                              // D = 0.0
}

private double controlSignal (double _control[], double _value, double _ref) {
  // Proportional action : Kp*(b*ref - value)
  double P = _control[1]*(_control[2]*_ref - _value);

  // Derivative action : D = ad*D - Bd*(h-h_old);
  _control[10] = _control[7]*_control[10] - _control[8]*(_value - _control[0]); 

  double output;
  // pControl is a global variable that, when false, forces to ignore the integral action
  //if (pControl) output = P + _control[11]; // P + D
  //else output = P + _control[9] + _control[10]; // P + I + D
  if (Ti == 0){ 
      output = P + _control[10]; // P + D
  }else{
      output = P + _control[9] + _control[10]; // P + I + D
  } 

  // Saturation of control signal
  double outputSat;
  if (output<_control[3])  outputSat = _control[3];
  else if (output>_control[4])  outputSat = _control[4];
  else outputSat = output;

  if (manualMode) {
      _control[9]=manualVh;
  } else {
      // Update integral action  :  I = I + Bi*(Hr-h) + Ar*(v-u)
      _control[9] = _control[9] + _control[5] * (_ref - _value) + _control[6]*(outputSat-output);
  }

  // keep the value for the next time
  _control[0] = _value;
  return outputSat;
}

private void changeSaturation (double _control[], double _min, double _max) {
  _control[3] = _min;
  _control[4] = _max;
}

public void colored () {

      T[0] = linear_T1;
      T[(colors/2)] = linear_T2;
      T[(colors-1)] = linear_T3;

      for (int k=1;k<(colors/2);k++) {
          T[k]=T[k-1]-((T[0]-T[colors/2])/((colors/2)-1));
      }
      for (int z=((colors/2)+1);z<(colors-1);z++) {
          T[z]=T[z-1]-((T[colors/2]-T[(colors-1)])/((colors/2)-2));
      }

      for (int j=0; j<colors; j++) {
          red = 255 - (int)(deltaColor * (maxTemp - T[j]));
          _red = 255 - (int)(deltaColorHeat * (vmax1 - linear_Vh));                  
          if (red>255) {red=255;}
          if (red<0) {red=0;}
          levelColors[j] = new java.awt.Color (red, 255-red, 255-red, red);
          colorHeater = new java.awt.Color (_red, _red, 0, 255);                         
      }       
}

